import React from "react";
import { Fade } from "react-reveal";
import { NavLink } from "react-router-dom";
import Navbar from "../components/Header";
import "../../styles/LandingPage.scss";
import "../../styles/LoadingScreen.css";
import Footer from "../components/Footer";
// import graphic from "../../static/media/test-about3.png";
import arrow_right from "../../static/media/arrow.svg";
import download_icon from "../../static/media/download.svg";
import portrait from "../../static/media/2.jpg";

const LandingPage = () => {
  return (
    <React.Fragment>
      <Navbar />
      <div
        id="download-button"
        onClick={() => {
          window.location.href =
            "https://drive.google.com/file/d/14cTDRopY11iwYzCRlH7CuPGrbMFtJBJw/view?usp=sharing";
        }}
      >
        <p>Download Resume</p>
        <img src={download_icon} alt="" />
      </div>
      <div className="landing-page-parent">
          <Fade top>
            <h1 className=" large-text-header">
              Data
              <br />
              Analyst
            </h1>
          </Fade>
        <Fade cascade>
          <div className="landing-text-area">
            {/* <h1 className="large-text">
              Data             
              <br />
              Analyst
            </h1> */}
            <div className="buttons">
              <h1 className="name">
                Matt Ly
              </h1>
              <img src={portrait} className="landing-pic" alt="" />
            </div>
            <div className="buttons">
              <NavLink to="/experience" exact>
                <div className="experience-button">
                  <p>View Experience</p>
                  <img src={arrow_right} alt="" className="arrow-right" />
                </div>
              </NavLink>

              <div
                className="outline-button"
                onClick={() => {
                  window.location.href = "mailto:mattlyldc21@gmail.com";
                }}
              >
                <p>Let's talk!</p>
              </div>
            </div>
          </div>
        </Fade>

        <div className="footer-div">
          <Footer />
        </div>
      </div>
    </React.Fragment>
  );
};

export default LandingPage;
