import React, { useState } from "react";
import ReactModal from 'react-modal';
import arrow_right from "../../static/media/arrow.svg";
import { Redirect } from 'react-router-dom';
import axios from 'axios';

const AUTH_URL = "https://accounts.spotify.com/authorize";
const CLIENT_ID = "YOUR_SPOTIFY_CLIENT_ID";
const REDIRECT_URI = "http://localhost:3000/callback";
const SCOPES = ["user-read-private", "user-read-email"];

function LoginButton() {

    const Login = () => {
        const handleLogin = () => {
            const queryParams = new URLSearchParams({
                client_id: "e1fbd36d01ee46249c7043c1aa6731c0",
                redirect_uri: REDIRECT_URI,
                scope: SCOPES.join(" "),
                response_type: "code",
            });

            const loginUrl = `${AUTH_URL}?${queryParams}`;
            window.location.replace(loginUrl);
        };
    
        // Check if the URL contains an authorization code after Spotify login
        const code = new URLSearchParams(window.location.search).get("code");
        if (code) {
            axios.post("/api/login", { code }).then((response) => {
            console.log(response.data);
            });
            return <Redirect to="/callback" />;
        }
    
        return (
            <div>
            <h1>Spotify Login</h1>
            <button onClick={handleLogin}>Login with Spotify</button>
            </div>
        );
    };

